$(document).ready(function(){
	$("#ctrl").click(function () {
	    $("#ctrlMenu").slideToggle(250, function () {
	    		$("#ctrl").css('text-decoration', $("#ctrlMenu").is(":visible") ? 'underline' : 'none');
		});
  });
	$("#vol").click(function () {
	    $("#volMenu").slideToggle(250, function () {
	    		$("#vol").css('text-decoration', $("#volMenu").is(":visible") ? 'underline' : 'none');
		});
  });
	$("#muteMusic").click(function () {
		if ($("#muteMusic").is(':checked')){
			music.muted = true;
		} else {
			if ($("#muteMaster").is(':checked')){
				music.muted = true;
			} else {
				music.muted = false;
			}
			intro.muted = true;
		}
	});
	$("#muteMaster").click(function () {
		if ($("#muteMaster").is(':checked')){
			music.muted = true;
			ASSET_MANAGER.sounds.forEach(function (sound) {
				sound.muted = true;
			});
		} else {
			if (!$("#muteMusic").is(':checked')){
				music.muted = false;
			}
			ASSET_MANAGER.sounds.forEach(function (sound) {
				sound.muted = false;
			});
		}
	});
	$("#volMusic").change(function () {
		console.log(document.getElementById("volMusic").value)
		console.log()
		music.volume = (document.getElementById("volMusic").value/100) * (document.getElementById("volMaster").value/100);
	});
	$("#volMaster").change(function () {
		ASSET_MANAGER.sounds.forEach(function (sound) {
			sound.volume = (document.getElementById("volMaster").value/100);
		});
		music.volume = (document.getElementById("volMusic").value/100) * (document.getElementById("volMaster").value/100);
		intro.muted = true;
	});
});

function fadeAudio(element, vol){
	$(element).animate({volume: vol}, 2000);
}
